import Head from "next/head";
import { TreeView, TreeItem } from "@material-ui/lab";
import { MdExpandMore, MdChevronRight } from "react-icons/md";
import uuid from "uuid/v4";

const useStyles = makeStyles({
  padding: {
    padding: "32px"
  }
});

const literalToTreeItem = (literal, classes) => {
  switch (typeof literal) {
    case "function":
      return (
        <TreeItem
          nodeId={uuid()}
          label={<pre class="prettyprint lang-js">{literal.toString()}</pre>}
        />
      );
    case "string":
      return (
        <TreeItem
          nodeId={uuid()}
          label={<pre class="prettyprint lang-js">{literal.toString()}</pre>}
        />
      );
    default:
      return <TreeItem nodeId={uuid()} label={literal.toString()} />;
  }
};

const objectToTreeItem = (object, classes) =>
  Object.entries(object).map(([key, value]) => (
    <TreeItem nodeId={uuid()} label={key.toString()}>
      {toTreeItems(value)}
    </TreeItem>
  ));

const toTreeItems = (thing, classes) => {
  if (typeof thing === "object") {
    // don't render empty objects
    if (JSON.stringify(thing) === "{}") return <></>;
    else return objectToTreeItem(thing);
  } else return literalToTreeItem(thing);
};

export const ObjectTree = ({ object, className }) => {
  const classes = useStyles();
  return (
    <>
      <Head>
        <script src="https://cdn.jsdelivr.net/gh/google/code-prettify@master/loader/run_prettify.js"></script>
      </Head>
      <TreeView
        defaultCollapseIcon={<MdExpandMore />}
        defaultExpandIcon={<MdChevronRight />}
        className={className}
      >
        {toTreeItems(object, classes)}
      </TreeView>
    </>
  );
};
