import Head from "next/head";
import { useTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { makePageTitle } from "../utils/helpers";

const useStyles = makeStyles({
  pre: {
    // fontSize: "16px"
  },
  explainer: {
    padding: "32px"
  }
});

export default () => {
  const theme = useTheme();
  const classes = useStyles();

  return (
    <>
      <Head>
        <title>{makePageTitle("Palette")}</title>
      </Head>
      <p className={classes.explainer}>
        Below is the theme object used in this project. Any and all styling
        should be driven by this object. If for some <strong>extreme</strong>{" "}
        reason, you need to inject styles outside this theme, please comment the
        offending code with a clear reason and your name. Additionally, while
        style injections should be avoided in general, when styles outside of
        this theme object are injected they should be done so via{" "}
        <code>import {makeStyles} from "@material-ui/core/styles";</code> rather
        than inline jss,{" "}
        <code>{"<style jsx>{`.selector { color: red; }`}</style>"}</code>, or
        (even worse) style props. Finally, if you believe something should be
        added to this blurb please bring it up with the team at your next
        standup.
      </p>
      <hr />
      <pre className={classes.pre}>{JSON.stringify(theme, null, 2)}</pre>
    </>
  );
};
