import Head from "next/head";
import { useTheme } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { makePageTitle } from "../utils/helpers";

const useStyles = makeStyles({
  pre: {
    // fontSize: "16px"
  },
  explainer: {
    padding: "32px"
  }
});

export default () => {
  const theme = useTheme();
  const classes = useStyles();

  return (
    <>
      <Head>
        <title>{makePageTitle("Palette")}</title>
      </Head>
      <p className={classes.explainer}>
        Below is the theme object used in this project. Any and all styling
        should be driven by this object. If for some <strong>extreme</strong>{" "}
        reason, you need to inject styles outside this theme, please comment it
        with a clear reason and your name.
      </p>
      <hr />
      <pre className={classes.pre}>{JSON.stringify(theme, null, 2)}</pre>
    </>
  );
};
