import { TreeView, TreeItem } from "@material-ui/lab";
import { MdExpandMore, MdChevronRight } from "react-icons/md";
import uuid from "uuid/v4";

const literalToTreeItem = literal => {
  if (literal.startsWith("function ")) {
    <TreeItem nodeId={uuid()} label={literal.toString()} />;
  } else return <TreeItem nodeId={uuid()} label={literal.toString()} />;
};

const objectToTreeItem = object => {
  const entries = Object.entries(object);

  return entries.map(([key, value]) => {
    // if (typeof value !== "object")
    //   return (
    //     <TreeItem
    //       nodeId={uuid()}
    //       label={`${key.toString()} = ${value.toString()}`}
    //     />
    //   );
    // else
    return (
      <TreeItem nodeId={uuid()} label={key.toString()}>
        {toTreeItems(value)}
      </TreeItem>
    );
  });
};

const toTreeItems = thing => {
  if (typeof thing === "object") {
    if (JSON.stringify(thing) === "{}") return <></>;
    else return objectToTreeItem(thing);
  } else return literalToTreeItem(thing);
};

export const ObjectTree = ({ object }) => (
  <TreeView
    defaultCollapseIcon={<MdExpandMore />}
    defaultExpandIcon={<MdChevronRight />}
  >
    {toTreeItems(object)}
  </TreeView>
);
