import Head from "next/head";
import { TreeView, TreeItem } from "@material-ui/lab";
import { MdExpandMore, MdChevronRight } from "react-icons/md";
import uuid from "uuid/v4";

const literalToTreeItem = literal => {
  switch (typeof literal) {
    case "function":
      return (
        <TreeItem
          nodeId={uuid()}
          label={<pre class="prettyprint lang-js">{literal.toString()}</pre>}
        />
      );
    case "string": {
      //
      // support for coloring rgba strings the color they encode is off for re-usability
      //
      // if (literal.startsWith("rgba(") && literal.endsWith(")"))
      //   return (
      //     <>
      //       <div className="rgba-wrapper">
      //         <TreeItem nodeId={uuid()} label={literal} />
      //       </div>
      //       {/*
      //        * Using jss because it's the cleanest way to inject these styles.
      //        * makeStyles doesn't work because it's a hook & style prop is highly discouraged
      //        */}
      //       <style jsx>{`
      //         .rgba-wrapper {
      //           color: ${literal};
      //         }
      //       `}</style>
      //     </>
      //   );
      return <TreeItem nodeId={uuid()} label={literal} />;
    }
    default:
      return <TreeItem nodeId={uuid()} label={literal.toString()} />;
  }
};

const objectToTreeItem = (object, _literalToTreeItem) =>
  Object.entries(object).map(([key, value]) => (
    <TreeItem nodeId={uuid()} label={key.toString()}>
      {toTreeItems(value, _literalToTreeItem)}
    </TreeItem>
  ));

const toTreeItems = (thing, _literalToTreeItem) => {
  const literalToTreeItem =
    _literalToTreeItem === undefined ? literalToTreeItem : _literalToTreeItem;

  if (typeof thing === "object") {
    // don't render empty objects
    if (JSON.stringify(thing) === "{}") return <></>;
    else return objectToTreeItem(thing, literalToTreeItem);
  } else return literalToTreeItem(thing);
};

export const ObjectTree = ({ object, literalToTreeItem, className }) => {
  return (
    <>
      <Head>
        <script src="https://cdn.jsdelivr.net/gh/google/code-prettify@master/loader/run_prettify.js"></script>
      </Head>
      <TreeView
        defaultCollapseIcon={<MdExpandMore />}
        defaultExpandIcon={<MdChevronRight />}
        className={className}
      >
        {toTreeItems(object, literalToTreeItem)}
      </TreeView>
    </>
  );
};
