import Head from "next/head";
import { TreeItem } from "@material-ui/lab";
import { useTheme, Divider } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import uuid from "uuid/v4";
import { makePageTitle } from "../utils/helpers";
import { ObjectTree } from "../components/ObjectTree";

const useStyles = makeStyles({
  padding: {
    padding: "32px"
  },
  preBlock: {
    margin: "16px",
    borderRadius: "4px",
    fontFamily: "Source Code Pro",
    border: "1px solid black",
    borderRadius: "4px",
    padding: "4px 10px",
    overflowX: "scroll"
  }
});

export default () => {
  const theme = useTheme();
  const classes = useStyles();

  return (
    <>
      <Head>
        <title>{makePageTitle("Palette")}</title>
      </Head>
      <div className={classes.wrapper}>
        <p className={classes.padding}>
          Below is the theme object used in this project. Any and all styling
          should be driven by this object. If for some <strong>extreme</strong>{" "}
          reason, you need to inject styles outside this theme, please comment
          the offending code with a clear reason and your name. Additionally,
          while style injections should be avoided in general, when styles
          outside of this theme object are injected they should be done so via{" "}
          <code>import {makeStyles} from "@material-ui/core/styles";</code>{" "}
          rather than inline jss,{" "}
          <code>{"<style jsx>{`.selector { color: red; }`}</style>"}</code>, or
          (even worse) style props. Finally, if you believe something should be
          added to this blurb please bring it up with the team at your next
          standup. Code and style standards are an evolving thing and they only
          get better when devs speak their minds.
        </p>
        <ObjectTree
          object={theme}
          customLiteralToTreeItem={literal => {
            switch (typeof literal) {
              case "function":
                return (
                  <>
                    <Head>
                      <link
                        href="https://fonts.googleapis.com/css?family=Source+Code+Pro&display=swap"
                        rel="stylesheet"
                      />
                    </Head>
                    <TreeItem
                      nodeId={uuid()}
                      label={
                        <pre className={`${classes.preBlock}`}>
                          <code
                            className={`language-javascript ${classes.codeBlock}`}
                          >
                            {literal.toString()}
                          </code>
                        </pre>
                      }
                    />
                  </>
                );
              case "string": {
                if (literal.startsWith("rgba(") && literal.endsWith(")"))
                  return (
                    <>
                      <div className="rgba-wrapper">
                        <TreeItem nodeId={uuid()} label={literal} />
                      </div>
                      {/*
                       * Using jss because it's the cleanest way to inject these styles.
                       * makeStyles doesn't work because it's a hook & style prop is highly discouraged
                       */}
                      <style jsx>{`
                        .rgba-wrapper {
                          color: ${literal};
                        }
                      `}</style>
                    </>
                  );
                return <TreeItem nodeId={uuid()} label={literal} />;
              }
              default:
                return <TreeItem nodeId={uuid()} label={literal.toString()} />;
            }
          }}
          className={classes.padding}
        />
      </div>
    </>
  );
};
